import React, {useState} from "react";
import {Algorithm} from "./algorithm_interface";
import { motion } from "framer-motion";

export class MinHeap implements Algorithm {

    display() {

        return (
            <div>

            </div>
        )
    }
}

const TreeNode = ({ label, children }: { label: string; children?: React.ReactNode }) => {
    const [isOpen, setIsOpen] = useState(false);

    return (
        <div className="pl-4 border-l-2 border-gray-300">
            <button
                onClick={() => setIsOpen(!isOpen)}
                className="flex items-center space-x-2 text-gray-700 font-medium"
            >
                <span>{isOpen ? "▼" : "▶"}</span> {/* Arrow Toggle */}
                <span>{label}</span>
            </button>

            <motion.div
                initial={false}
                animate={{ height: isOpen ? "auto" : 0, opacity: isOpen ? 1 : 0 }}
                transition={{ duration: 0.3, ease: "easeInOut" }}
                className="overflow-hidden"
            >
                {children}
            </motion.div>
        </div>
    );
};
