import { Algorithm } from "./algorithm_interface";
import {BinaryTree} from "../structures/binary_tree";
import {AnimatedTree} from "../animations/tree_animation";
import {useState} from "react";

export class MinHeap implements Algorithm {
    tree: BinaryTree;

    constructor() {
        this.tree = new BinaryTree();
    }

    insert(value: number | string) {
        this.tree.add(value);
    }

    display(): JSX.Element {

        const [treeState, setTreeState] = useState(this.tree.values);

        return (
            <svg width="500" height="500">
                <AnimatedTree tree={treeState} />
            </svg>
        );
    }
}
