import React, { useState } from "react";
import { Algorithm } from "./algorithm_interface";
import { useTransition, animated } from '@react-spring/web';

// Define TreeNode type
interface TreeNode {
    id: string;
    label: string | number;
    x: number;
    y: number;
    children: TreeNode[];
}

// Initial tree data
const initialTreeData: TreeNode = {
    id: "1",
    label: "Root",
    x: 250,
    y: 50,
    children: [
        {
            id: "2",
            label: "Child 1",
            x: 150,
            y: 150,
            children: [
                { id: "3", label: "Child 1.1", x: 100, y: 250, children: [] }
            ]
        },
        {
            id: "3",
            label: "Child 2",
            x: 350,
            y: 150,
            children: []
        }
    ]
};

// Function to add a new child to a node
const addChild = (parentId: string, newChild: TreeNode, treeData: TreeNode, setTreeData: React.Dispatch<React.SetStateAction<TreeNode>>) => {
    const updatedTree = (node: TreeNode): TreeNode => {
        if (node.id === parentId) {
            // Ensure `children` is always an array
            const updatedChildren = node.children ? [...node.children, newChild] : [newChild];
            return { ...node, children: updatedChildren };
        }

        if (node.children) {
            return { ...node, children: node.children.map(updatedTree) };
        }

        return node;
    };

    setTreeData(updatedTree(treeData));
};

// AnimatedTree component for rendering nodes and edges
const AnimatedTree = ({ data }: { data: TreeNode }) => {
    // Transitions for animating each node
    const nodeTransitions = useTransition([data], {
        keys: (item) => item.id,
        from: { opacity: 0, transform: 'scale(0)' },
        enter: { opacity: 1, transform: 'scale(1)' },
        leave: { opacity: 0, transform: 'scale(0)' },
    });

    // Transitions for animating edges
    const edgeTransitions = useTransition(data.children || [], {
        keys: (item) => item.id,
        from: { strokeOpacity: 0 },
        enter: { strokeOpacity: 1 },
        leave: { strokeOpacity: 0 },
    });

    return (
        <g>
            {/* Render the edges (lines) */}
            {edgeTransitions((style, item) => (
                <animated.line
                    x1={data.x}
                    y1={data.y}
                    x2={item.x}
                    y2={item.y}
                    stroke="black"
                    strokeWidth="2"
                    style={style}
                />
            ))}

            {/* Render the node (circle) */}
            {nodeTransitions((style, item) => (
                <animated.g style={style}>
                    <circle cx={item.x} cy={item.y} r={30} fill="steelblue" />
                    <text x={item.x} y={item.y} dy={5} textAnchor="middle" fill="white" fontSize="12">
                        {item.label}
                    </text>
                </animated.g>
            ))}

            {/* Recursively render child nodes */}
            {data.children && data.children.map((child) => (
                <AnimatedTree key={child.id} data={child} />
            ))}
        </g>
    );
};

// MinHeap functional component
const MinHeap: React.FC = () => {
    const [treeData, setTreeData] = useState<TreeNode>(initialTreeData);

    // Function to insert a new child node
    const insert = (value: string | number) => {
        const newChild: TreeNode = {
            id: `${Math.random()}`, // Unique id for the new node
            label: value,
            x: 200,
            y: 200,
            children: [],
        };

        // Add new child to the root node
        addChild("1", newChild, treeData, setTreeData);
    };

    return (
        <div>
            <svg width="500" height="500">
                <AnimatedTree data={treeData} />
            </svg>
            <button onClick={() => insert("New Child")}>Add Child</button>
        </div>
    );
};

export default MinHeap;
