import React, {useEffect, useState} from "react";
import * as d3 from "d3";
import {BaseType} from "d3";

export const Testing: React.FC = () => {

    const [instructionStack, setInstructionStack] = useState<Instruction[]>([]);

    type Instruction = {
        type: 'add' | 'swap';
        value?: string | number;
        index?: number;
        fromIndex?: number;
        toIndex?: number;
    };

    useEffect(() => {
        const newInstructions: Instruction[] = [
            { type: 'add', value: '1', index: 0 },
            { type: 'add', value: '2', index: 1 },
            { type: 'add', value: '3', index: 2 },
        ];
        setInstructionStack(newInstructions);
    }, []);

    const processInstructions = (svg: d3.Selection<BaseType, unknown, HTMLElement, any>) => {
        instructionStack.forEach((instruction) => {
            if (instruction.type === 'add' && instruction.value) {
                // Add a new node for 'add' instruction
                const nodeElement = node(svg);

                // Animate the node: fade in and move
                nodeElement.transition()
                    .duration(1000)  // Duration of 1 second
                    .style("opacity", 1)  // Fade in the node
                    .attr("cx", 100 + (instruction.index || 0) * 50)  // Place based on index, space out the nodes
                    .attr("cy", 100);  // Keep y position constant for simplicity
            }
        });
    };

    // Node function that appends a circle and returns the D3 selection
    const node = (svg: d3.Selection<BaseType, unknown, HTMLElement, any>) => {
        return svg.append("circle")  // Return the D3 selection after appending
            .attr("cx", 200)
            .attr("cy", 200)
            .attr("r", 20)
            .attr("class", "node")
            .style("opacity", 0);  // Start invisible
    };

    useEffect(() => {
        // Select the SVG element that the D3 code will operate on
        const svg = d3.select("#svg-container")
            .attr("width", 500)
            .attr("height", 500);

        // Process the instructions to add nodes
        processInstructions(svg);
    }, [instructionStack]);   // Empty dependency array to run the effect only once

    return null;  // No need to return an element here, as we're using D3 to manipulate the SVG
};
