import {Algorithm} from "./algorithm_interface";
import { useTransition, animated } from '@react-spring/web';

export class MinHeap implements Algorithm {

    display() {

        return (
            <div>
                <AnimatedTree data={treeData} />;
            </div>
        )
    }
}

interface TreeNode {
    id: string;
    label: string;
    x: number;
    y: number;
    children?: TreeNode[];
}

const treeData: TreeNode = {
    id: "1",
    label: "Root",
    x: 250,
    y: 50,
    children: [
        { id: "2", label: "Child 1", x: 150, y: 150 },
        { id: "3", label: "Child 2", x: 350, y: 150, children: [
                { id: "4", label: "Child 2.1", x: 300, y: 250 },
                { id: "5", label: "Child 2.2", x: 400, y: 250 },
            ]}
    ]
};


const AnimatedTree = ({ data }: { data: TreeNode }) => {
    // Use transitions for animating each node
    const transitions = useTransition(data.children || [], {
        keys: (item) => item.id,
        from: { opacity: 0, transform: 'translateY(20px)' },
        enter: { opacity: 1, transform: 'translateY(0)' },
        leave: { opacity: 0, transform: 'translateY(20px)' },
    });

    return (
        <div style={{ paddingLeft: 20 }}>
            <div>{data.label}</div>
            {transitions((style, item) => (
                <animated.div style={style}>
                    <AnimatedTree data={item} />
                </animated.div>
            ))}
        </div>
    );
};
