import React, {useState} from "react";
import {Algorithm} from "./algorithm_interface";
import { motion } from "framer-motion";

export class MinHeap implements Algorithm {

    display() {

        return (
            <div>
                <TreeNode node={treeData} />
            </div>
        )
    }
}

// Type definition for nodes
type TreeNodeProps = {
    label: string;
    children?: TreeNodeProps[];
};

// Tree data
const treeData: TreeNodeProps = {
    label: "Root",
    children: [
        {
            label: "Child 1",
            children: [
                { label: "Subchild 1" },
                { label: "Subchild 2" }
            ]
        },
        {
            label: "Child 2",
            children: [{ label: "Subchild 3" }]
        }
    ]
};

// Recursive component for tree nodes
const TreeNode = ({ node }: { node: TreeNodeProps }) => {
    const [isOpen, setIsOpen] = useState(false);

    return (
        <div className="relative ml-6">
            {/* Node */}
            <motion.div
                className="flex items-center space-x-2 cursor-pointer bg-blue-500 text-white px-3 py-2 rounded-full shadow-md"
                onClick={() => setIsOpen(!isOpen)}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
            >
                <span>{isOpen ? "▼" : "▶"}</span> {/* Expand/collapse arrow */}
                <span>{node.label}</span>
            </motion.div>

            {/* Connecting Line */}
            {node.children && (
                <motion.div
                    className="absolute left-0 top-1/2 w-6 border-l-2 border-gray-400"
                    initial={{ height: 0 }}
                    animate={{ height: isOpen ? "20px" : "0px" }}
                    transition={{ duration: 0.3 }}
                />
            )}

            {/* Children (Collapse & Expand) */}
            <motion.div
                initial={false}
                animate={{ height: isOpen ? "auto" : 0, opacity: isOpen ? 1 : 0 }}
                transition={{ duration: 0.3 }}
                className="ml-4 border-l-2 border-gray-400 overflow-hidden pl-2"
            >
                {node.children &&
                    node.children.map((child, index) => (
                        <TreeNode key={index} node={child} />
                    ))}
            </motion.div>
        </div>
    );
};
