import { Algorithm } from "./algorithm_interface";
import {BinaryTree} from "../structures/binary_tree";
import React, {useReducer, useState} from "react";
import {animated, useTransition} from "@react-spring/web";

export const MinHeap: Algorithm = {

    values: [] as (number | string)[],

    insert(value: string | number) {
        MinHeap.values.push(value);
        const [_, forceUpdate] = useReducer(x => x + 1, 0);
    },

    render(): JSX.Element {

        return (
            <svg width="500" height="500">
                <AnimatedTree values={MinHeap.values}/>
            </svg>
        );
    }
};

const AnimatedTree = ({values}: {values : (string | number)[]}) => {



    const nodeTransitions = useTransition(values, {
        keys: (item) => `${item}`,  // Ensure unique keys for each item
        from: { opacity: 0, transform: 'scale(0)' },
        enter: { opacity: 1, transform: 'scale(1)' },
        leave: { opacity: 0, transform: 'scale(0)' },
    });

    const getNodePosition = (index: number) => {
        const x = 100 + index * 150;  // Example horizontal position
        const y = 100;  // All nodes aligned at the same vertical position
        return { x, y };
    };

    return (
        <g>
            {/* Render the animated nodes */}
            {nodeTransitions((style, node, index) => {
                const posIndex = index as unknown as number; // Ensure index is treated as a number
                const {x, y} = getNodePosition(posIndex); // Get position for each node
                return (
                    <animated.g key={node} style={style}>
                        {/* Render edges (lines) */}
                        {posIndex > 0 && (
                            <animated.line
                                x1={getNodePosition(Math.floor((posIndex - 1) / 2)).x} // Parent's x
                                y1={getNodePosition(Math.floor((posIndex - 1) / 2)).y} // Parent's y
                                x2={x}
                                y2={y}
                                stroke="black"
                                strokeWidth="2"
                                style={{strokeOpacity: 1}}
                            />
                        )}

                        {/* Render node (circle) */}
                        <circle cx={x} cy={y} r={30} fill="steelblue"/>
                        <text x={x} y={y} dy={5} textAnchor="middle" fill="white" fontSize="12">
                            {node}
                        </text>
                    </animated.g>
                );
            })}
        </g>
    );
};
