import { Algorithm } from "./algorithm_interface";
import {BinaryTree} from "../structures/binary_tree";
import {animated, SpringValue, useSprings, useTransition} from "@react-spring/web";
import {useEffect, useState} from "react";
import {useSpring} from "framer-motion";

export class MinHeap implements Algorithm {

    private tree : BinaryTree;

    constructor(
        private isDynamicSize: boolean
    ) {
       this.tree = new BinaryTree();
    }

    insert(value: string | number) : void {

        this.tree?.add(value);

        this.heapify(this.tree.length);

        //alert(this.tree?.get_current_height())

        //if (this.isDynamicSize && current_height > this.current_max_height) this.current_max_height = current_height + 1;

    }

    delete(value: string | number) : void {

    }

    render(): JSX.Element {

        //alert(this.current_max_height);

        return (
            <svg width="500" height="500">
                <AnimatedTree values={this.tree.values} height={1}/>
            </svg>
        );
    }

    heapify(cur_index : number): void {

        // At root, do not heapify.
        if (cur_index == 1) return;

        // Check if parent is less than the current index
        const cur = this.tree.get(cur_index);
        const par_index = this.tree.get_parent(cur_index);
        const par_val = this.tree.get(par_index);

        if (cur >= par_val) return;

        // If so swap the indexes
        this.tree.swap(cur_index, par_index);

        // Heapify on the new cur index
        this.heapify(par_index);
    }
}

type Instruction = {
    type: 'add' | 'swap';
    value?: string | number;
    index?: number;
    fromIndex?: number;
    toIndex?: number;
};

const AnimatedTree = ({ values, height }: { values: (string | number)[], height: number }) => {
    const [instructionStack, setInstructionStack] = useState<Instruction[]>([]); // Stack of instructions
    const [currentValues, setCurrentValues] = useState(values); // Current values in the tree
    const [positions, setPositions] = useState<{ [key: number]: { x: number; y: number } }>({});

    const [currentInstruction, setCurrentInstruction] = useState<Instruction>();

    // Simulate the stack of instructions (for testing purposes)
    useEffect(() => {
        const newInstructions: Instruction[] = [
            { type: 'add', value: '1', index: 0 },
            { type: 'add', value: '2', index: 1 },
            { type: 'add', value: '0', index: 2 },
            { type: 'swap', fromIndex: 0, toIndex: 2 },
        ];
        setInstructionStack(newInstructions);
    }, []);

    // Transition based on the current values and the instruction stack
    const nodeTransitions = useTransition(currentValues.map((value, i) => ({ value, index: i })), {
        keys: (item) => `${item.value}-${item.index}`,
        from: { opacity: 0, transform: 'scale(0)' },
        enter: { opacity: 1, transform: 'scale(1)' },
        leave: { opacity: 0, transform: 'scale(0)' },
        initial: null,
        trail: 300,
        config: { duration: 1000 },
    });

    const [springs, api] = useSprings(currentValues.length, (index) => ({
        x: nodePositions(index).x,
        y: nodePositions(index).y,
        config: { duration: 500 },
    }));

    // When updating positions (e.g., during a swap)
    useEffect(() => {
        api.start((index) => ({
            x: nodePositions(index).x,
            y: nodePositions(index).y,
        }));
    }, [currentValues, api]); // Ensure animation updates when currentValues change


    const getNodePosition = (index: number): { x: number, y: number } => {
        if (index < 0 || isNaN(index)) return { x: 0, y: 0 };
        if (index === 0) return { x: 250, y: 50 }; // Root at the center

        const depth = Math.floor(Math.log2(index + 1)); // Get depth in the tree
        const xSpacing = (200) / (depth + 1); // Adjust x spacing
        const ySpacing = 80; // Vertical spacing

        const parentIndex = Math.floor((index - 1) / 2);
        const parentPos = getNodePosition(parentIndex);

        const isLeftChild = index % 2 === 1;
        const xOffset = isLeftChild ? -xSpacing : xSpacing;

        return { x: parentPos.x + xOffset, y: parentPos.y + ySpacing };
    };

    const delay = (ms: number) => new Promise(resolve => setTimeout(resolve, ms));

    const processInstructions = async () => {
        for (let instruction of instructionStack) {

            setCurrentInstruction(instruction);

            // Handle the "add" instruction
            if (instruction.type === 'add' && typeof instruction.index === 'number') {
                await new Promise<void>((resolve) => {
                    setCurrentValues((prev) => {
                        const newValues = [...prev];
                        if (instruction.index != null && instruction.value != undefined) {
                            newValues.splice(instruction.index, 0, instruction.value);
                        }
                        resolve(); // Resolve after the update
                        return newValues;
                    });
                });

                // Add a 1-second delay before processing the next instruction
                await delay(1000); // Delay for 1 second
            }

            // Handle the "swap" instruction
            if (instruction.type === 'swap' && typeof instruction.fromIndex === 'number' && typeof instruction.toIndex === 'number') {
                await new Promise<void>((resolve) => {
                    // Get the current positions of the two nodes to swap
                    const fromPos = getNodePosition(instruction.fromIndex as number);
                    const toPos = getNodePosition(instruction.toIndex as number);

                    // Animate the swap
                    setPositions((prev) => ({
                        ...prev,
                        [instruction.fromIndex as number]: toPos,
                        [instruction.toIndex as number]: fromPos,
                    }));

                    // Perform the swap on currentValues after the animation has time to run
                    setCurrentValues((prev) => {
                        const newValues = [...prev];
                        [newValues[instruction.fromIndex as number], newValues[instruction.toIndex as number]] = [
                            newValues[instruction.toIndex as number],
                            newValues[instruction.fromIndex as number],
                        ];
                        resolve(); // Resolve after the swap
                        return newValues;
                    });
                });

                // Add a 1-second delay before processing the next instruction
                await delay(1000); // Delay for 1 second
            }
        }
    };

    const nodePositions = (index: number) => {
        return positions[index] || getNodePosition(index); // Use the swapped position or default position
    };

    // Ensure that state updates are processed in order by using this method within the useEffect
    useEffect(() => {
        const runInstructions = async () => {
            await processInstructions(); // Ensure all instructions are processed sequentially
        };

        runInstructions();
    }, [instructionStack]);

    interface SpringStyle {
        x: SpringValue<number>;
        y: SpringValue<number>;
    }

    return (
        <g>
            {/* Render edges (lines) first */}
            {nodeTransitions((style, { value, index }) => {
                if (index === 0) return null; // Root node has no parent, skip edge rendering
                const { x, y } = getNodePosition(index);
                const parentIndex = Math.floor((index - 1) / 2);
                const parentPos = getNodePosition(parentIndex);

                return (
                    <animated.line
                        key={`line-${index}`} // Separate key for lines
                        x1={parentPos.x}
                        y1={parentPos.y}
                        x2={x}
                        y2={y}
                        stroke="black"
                        strokeWidth="2"
                        style={{ strokeOpacity: 1 }}
                    />
                );
            })}

            {/* Render nodes (circles) on top of the lines */}
            {currentInstruction?.type === 'swap' ? (
                springs.map((style: SpringStyle, index: number) => (
                    <animated.g
                        key={index}
                        transform={style.x.to((x) => `translate(${x}px, ${style.y.get()}px)`)}>
                        <circle cx={0} cy={0} r={30} fill="steelblue" />
                        <text x={0} y={0} dy={5} textAnchor="middle" fill="white" fontSize="12">
                            {currentValues[index]}
                        </text>
                    </animated.g>
                ))

            ) : (
                nodeTransitions((style, { value, index }) => {
                    const { x, y } = nodePositions(index); // Use nodePositions instead of getNodePosition

                    return (
                        <animated.g key={`node-${index}`} style={style}>
                            <circle cx={x} cy={y} r={30} fill="steelblue" />
                            <text x={x} y={y} dy={5} textAnchor="middle" fill="white" fontSize="12">
                                {value}
                            </text>
                        </animated.g>
                    );
                })
            )}
        </g>
    );
};
