import {Instruction} from "./i_instruction";
import {BaseType, Selection} from "d3";
import * as d3 from "d3";

export class FAShowArrowInstruction implements Instruction {

    index: number;

    constructor(index: number) {
        this.index = index;
    }

    async process(svg: Selection<BaseType, unknown, HTMLElement, any>): Promise<void> {

        const lineId = `#input-${this.index}`;
        const arrowId = `#arrow-input-${this.index} path`;
        const duration = 300;

        // Fade in the line
        d3.select<SVGLineElement, unknown>(lineId)
            .transition()
            .duration(duration)
            .style("opacity", 1);

        // Fade in the arrowhead path
        d3.select<SVGPathElement, unknown>(arrowId)
            .transition()
            .duration(duration)
            .style("opacity", 1);


        return Promise.resolve(undefined);
    }

}
