import React, { useEffect } from "react";
import * as d3 from "d3";

export const Testing: React.FC = () => {
    useEffect(() => {
        // Select the SVG element that the D3 code will operate on
        const svg = d3.select("#svg-container")
            .attr("width", 500)
            .attr("height", 500);

        // Create and animate the first node (circle)
        const node1 = svg.append("circle")
            .attr("cx", 100)
            .attr("cy", 100)
            .attr("r", 20)
            .attr("class", "node")
            .style("opacity", 0);  // Start invisible

        node1.transition()
            .duration(1000)  // Duration of 1 second
            .style("opacity", 1)  // Fade in the node
            .attr("cx", 300)  // Move the node to (300, 100)
            .attr("cy", 100);

        // Create and animate the second node (circle)
        const node2 = svg.append("circle")
            .attr("cx", 200)
            .attr("cy", 200)
            .attr("r", 20)
            .attr("class", "node")
            .style("opacity", 0);  // Start invisible

        node2.transition()
            .duration(1000)  // Duration of 1 second
            .style("opacity", 1)  // Fade in the node
            .attr("cx", 400)  // Move the node to (400, 200)
            .attr("cy", 200);
    }, []);  // Empty dependency array to run the effect only once

    return null;  // No need to return an element here, as we're using D3 to manipulate the SVG
};
