import React from 'react';
import './App.css';

// Main Page -- What is displayed across all pages
function App() {
    return (
        <div className="h-screen bg-red-500 flex flex-col items-center justify-center">
            {/* Title Section */}
            <h1 className="text-4xl md:text-6xl font-bold text-white-800 mb-8">
                DAVE: Dynamic Algorithm Visualizer
            </h1>

            {/* Buttons Section */}
            <div className="flex gap-4">
                <button
                    onClick={() => window.location.href = '/about'}
                    className="bg-blue-500 text-white px-6 py-3 rounded-md text-lg shadow hover:bg-blue-600 transition transform hover:scale-105"
                >
                    Learn More
                </button>
                <button
                    onClick={() => window.location.href = '/visualizer'}
                    className="bg-green-500 text-white px-6 py-3 rounded-md text-lg shadow hover:bg-green-600 transition transform hover:scale-105"
                >
                    Start Visualizing
                </button>
            </div>
        </div>
    );
}

export default App;
