import {Instruction} from "./i_instruction";
import d3, {BaseType, Selection} from "d3";

export class FAHighlightInstruction implements Instruction {

    constructor(value: string | number) {

    }

    process(svg: d3.Selection<d3.BaseType, unknown, HTMLElement, any>): Promise<void> {
        // Ensure the line and its associated marker exist
        const line = svg.select("#line-0");
        const markerId = "arrow-0";

        if (!line.empty()) {
            // Apply transition to the line
            line.transition()
                .duration(1000)
                .attr("stroke", "red")
                .attr("stroke-width", 2)
                .attr("marker-end", `url(#${markerId})`)
                .on("end", function() {
                    // Check if the arrow path exists before applying the transition
                    const arrowPath = svg.select(`#${markerId} path`);
                    if (!arrowPath.empty()) {
                        arrowPath.transition()
                            .duration(1000)
                            .attr("fill", "red"); // Change the arrowhead color
                    } else {
                        console.log("Arrowhead path not found.");
                    }
                });
        } else {
            console.log("Line #line-0 not found.");
        }

        return Promise.resolve();
    }


}
