import {Instruction} from "./i_instruction";
import d3, {BaseType, Selection} from "d3";

export class FAHighlightInstruction implements Instruction {

    constructor(value: string | number) {

    }

    process(svg: Selection<BaseType, unknown, HTMLElement, any>): Promise<void> {

        const lineId = "line-0";  // This should match the line you want to target
        const markerId = `arrow-${lineId}`;  // Corresponding marker ID

        svg.select(`#${lineId}`)
            .transition()
            .duration(1000)
            .attr("stroke", "red")
            .attr("stroke-width", 2)
            .attr("marker-end", `url(#${markerId})`)  // Use the dynamically generated marker ID
            .on("end", function() {
                svg.select(`#${markerId} path`)  // Select the correct path in the marker
                    .transition()
                    .duration(1000)
                    .attr("fill", "red");  // Change arrowhead color to red
            });



        const line = svg.select("#line-0");
        console.log("Line found:", !line.empty());

        return Promise.resolve();
    }

}
