import { Algorithm } from "./algorithm_interface";
import React, { useState } from "react";
import { useTransition, animated } from '@react-spring/web';

interface TreeNode {
    id: string;
    label: string | number;
    x: number;
    y: number;
    children: TreeNode[];
}

const AnimatedTree = ({ data }: { data: TreeNode }) => {
    // Transitions for animating each node
    const nodeTransitions = useTransition([data], {
        keys: (item) => item.id,
        from: { opacity: 0, transform: 'scale(0)' },
        enter: { opacity: 1, transform: 'scale(1)' },
        leave: { opacity: 0, transform: 'scale(0)' },
    });

    // Transitions for animating edges (lines between nodes)
    const edgeTransitions = useTransition(data.children || [], {
        keys: (item) => item.id,
        from: { strokeOpacity: 0 },
        enter: { strokeOpacity: 1 },
        leave: { strokeOpacity: 0 },
    });

    return (
        <g>
            {/* Render the edges (lines) */}
            {edgeTransitions((style, item) => (
                <animated.line
                    x1={data.x}
                    y1={data.y}
                    x2={item.x}
                    y2={item.y}
                    stroke="black"
                    strokeWidth="2"
                    style={style}
                />
            ))}

            {/* Render the node (circle) */}
            {nodeTransitions((style, item) => (
                <animated.g style={style}>
                    <circle cx={item.x} cy={item.y} r={30} fill="steelblue" />
                    <text x={item.x} y={item.y} dy={5} textAnchor="middle" fill="white" fontSize="12">
                        {item.label}
                    </text>
                </animated.g>
            ))}

            {/* Recursively render child nodes */}
            {data.children && data.children.map((child) => (
                <AnimatedTree key={child.id} data={child} />
            ))}
        </g>
    );
};

export class MinHeap implements Algorithm {
    treeData: TreeNode;
    setTreeData: React.Dispatch<React.SetStateAction<TreeNode>>;

    constructor(treeData: TreeNode, setTreeData: React.Dispatch<React.SetStateAction<TreeNode>>) {
        this.treeData = treeData;
        this.setTreeData = setTreeData;
    }

    insert(value: number | string) {
        const newChild: TreeNode = {
            id: String(Date.now()), // Unique ID for the new child node
            label: value,
            x: 200,
            y: 200,
            children: [],
        };

        // Adding a new child to the root node (or customize logic)
        const updatedTree = this.addChild(this.treeData.children[1], newChild);
        this.setTreeData(updatedTree);
    }

    addChild(node: TreeNode, newChild: TreeNode): TreeNode {
        if (node.id === "1") { // Assuming we add to the root for simplicity
            const updatedChildren = [...node.children, newChild];
            return { ...node, children: updatedChildren };
        }

        if (node.children) {
            return { ...node, children: node.children.map(child => this.addChild(child, newChild)) };
        }

        return node;
    }

    display(): JSX.Element {
        return (
            <svg width="500" height="500">
                <AnimatedTree data={this.treeData} />
            </svg>
        );
    }
}
