import {Instruction} from "./i_instruction";
import * as d3 from "d3";
import {BaseType} from "d3";

export class TreeAddInstruction implements Instruction {

    index: number;
    value: number | string;

    constructor(index: number, value: number | string) {
        this.index = index;
        this.value = value;
    }

    async process(svg: d3.Selection<BaseType, unknown, HTMLElement, any>): Promise<void> {

        let nodesGroup = svg.select<SVGGElement>("g.nodes");

        if (nodesGroup.empty()) {
            nodesGroup = svg.append("g").attr("class", "nodes");
        }

        // Creates the circle node on screen
        const nodeElement = nodesGroup.append("circle")
            .attr("cx", 200)
            .attr("cy", 200)
            .attr("r", 20)
            .attr("class", "node")
            .style("opacity", 0);

        // Creates the text label on screen
        const label = nodesGroup.append("text")
            .attr("x", 200)
            .attr("y", 200)
            .attr("class", "label")
            .attr("text-anchor", "middle")
            .attr("alignment-baseline", "middle")
            .style("fill", "white")
            .style("font-size", "12px")
            .text(this.value);

        await nodeElement.transition()
            .duration(1000)
            .style("opacity", 1)
            .attr("cx", 100)
            .attr("cy", 100)
            .end();

        await label.transition()
            .duration(1000)
            .style("opacity", 1)
            .attr("x", 100)
            .attr("y", 100)
            .end();

    }

}
