import { Algorithm } from "./algorithm_interface";
import * as d3 from "d3";
import React, { Component } from "react";

// Interface for TreeNodeData
interface TreeNodeData {
    name: string;
    children?: TreeNodeData[];
}

const treeData: TreeNodeData = {
    name: "Root",
    children: [
        {
            name: "Child 1",
            children: [
                { name: "Child 1.1" },
                { name: "Child 1.2" },
            ],
        },
        {
            name: "Child 2",
            children: [{ name: "Child 2.1" }],
        },
    ],
};

export class MinHeap extends Component<{}, {}> implements Algorithm {
    private svgRef = React.createRef<SVGSVGElement>();

    renderTree = () => {
        const svg = d3.select(this.svgRef.current);
        const margin = { top: 20, right: 90, bottom: 30, left: 90 };
        const width = 960 - margin.left - margin.right;
        const height = 500 - margin.top - margin.bottom;

        svg
            .attr("width", width + margin.left + margin.right)
            .attr("height", height + margin.top + margin.bottom)
            .append("g")
            .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

        const treeLayout = d3.tree<TreeNodeData>().size([height, width]);

        const root = d3.hierarchy(treeData);
        const treeDataProcessed = treeLayout(root);

        svg
            .selectAll(".link")
            .data(treeDataProcessed.links())
            .enter()
            .append("line")
            .attr("class", "link")
            .attr("x1", (d) => d.source.x)
            .attr("y1", (d) => d.source.y)
            .attr("x2", (d) => d.target.x)
            .attr("y2", (d) => d.target.y)
            .attr("stroke", "#ccc")
            .attr("stroke-width", 2);

        const node = svg
            .selectAll(".node")
            .data(treeDataProcessed.descendants())
            .enter()
            .append("g")
            .attr("class", "node")
            .attr("transform", (d) => `translate(${d.x},${d.y})`);

        node
            .append("circle")
            .attr("r", 10)
            .attr("fill", "#69b3a2");

        node
            .append("text")
            .attr("dx", 12)
            .attr("dy", 3)
            .text((d: d3.HierarchyNode<TreeNodeData>) => d.data.name);
    };

    componentDidMount() {
        this.renderTree();
    }

    // Implementing the display method from the Algorithm interface
    display() {
        return (
            <div>
                <svg ref={this.svgRef}></svg>
            </div>
        );
    }

    render() {
        return this.display(); // Use display method to render the component
    }
}
