import { animated, useTransition } from "@react-spring/web";
import React from "react";

// AnimatedTree now receives values as props
export const AnimatedTree = ({ values }: { values: (string | number)[] }) => {

    const nodeTransitions = useTransition(values, {
        keys: (item) => item, // Use the item itself as the key (item is of type string | number)
        from: { opacity: 0, transform: 'scale(0)' },
        enter: { opacity: 1, transform: 'scale(1)' },
        leave: { opacity: 0, transform: 'scale(0)' },
    });

    const getNodePosition = (index: number) => {
        const x = 100 + index * 150; // Example horizontal position
        const y = 100; // All nodes aligned at the same vertical position
        return { x, y };
    };

    return (
        <g>
            {nodeTransitions((style, node, index) => {
                const posIndex = index as unknown as number;
                const { x, y } = getNodePosition(posIndex);
                return (
                    <animated.g key={node} style={style}>
                        {posIndex > 0 && (
                            <animated.line
                                x1={getNodePosition(Math.floor((posIndex - 1) / 2)).x}
                                y1={getNodePosition(Math.floor((posIndex - 1) / 2)).y}
                                x2={x}
                                y2={y}
                                stroke="black"
                                strokeWidth="2"
                                style={{ strokeOpacity: 1 }}
                            />
                        )}
                        <circle cx={x} cy={y} r={30} fill="steelblue" />
                        <text x={x} y={y} dy={5} textAnchor="middle" fill="white" fontSize="12">
                            {node}
                        </text>
                    </animated.g>
                );
            })}
        </g>
    );
};
